import { HttpErrorResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import{FormGroup,FormBuilder,Validators} from '@angular/forms'
import { Observable, Subject, catchError, from, mergeMap, of, switchMap, takeUntil, tap, throwError } from 'rxjs';
import { Post } from 'src/app/interface/interface.post';
import { PostService } from 'src/app/service/post.service';
@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css']
})
export class PostComponent{

postForm:FormGroup
posts$: Observable<Post[]>;
private destroy$:Subject<void> = new Subject<void>();
selectedFile: File;

constructor(private formBuilder:FormBuilder,private postService:PostService){}


ngOnInit():void{
  this.postForm = this.formBuilder.group({
    title:['',Validators.required],
    description:['',Validators.required]
  });
this.getPost();
}

onSubmit(){
  this.postService.addPost(this.postForm.value).pipe(
    switchMap(()=> this.getPost())
  ).subscribe(
    (response: Post[])=>{
      console.log(response);
      this.postForm.reset();
    },
    (error: HttpErrorResponse)=>{
      alert(error.message);
    }
  )
};


onDeletePost(postId:number):void{
  if(!confirm('Are you sure you want delete this post?')){
    return;
  }
  this.postService.deletePost(postId).pipe(
    takeUntil(this.destroy$),
    switchMap(()=> this.getPost())
  ).subscribe(
    (response: Post[])=>{
      console.log(response);
    },
    (error: HttpErrorResponse) => {
      alert(error.message);
    }
  )
}

getPost():Observable<Post[]>{
 return this.posts$ = this.postService.getAllPost().pipe(
  catchError(error => {
    console.log(error);
    return of([]);
  })
 );
}

ngOnDestroy(): void {
  this.destroy$.next();
  this.destroy$.complete();
}

}
<form [formGroup]="postForm" (ngSubmit)="onSubmit()">

<div>
    <label for="title">Title</label>
    <input type="text" id="title" formControlName="title">
</div>

<div>
    <label for="description">Description</label>
    <input type="text" id="description" formControlName="description">
</div>
<button type="submit" [disabled]="!postForm.valid">Add</button>
</form>

<div *ngFor="let post of posts$ | async as posts$">
    {{post.title}}
    {{post.description}}
    <button (click)="onDeletePost(post.id)">Delete</button>
</div>
